import { useLocation } from "react-router";

const Update = () => {

    const location = useLocation()
    const {post} = location.state

    console.log(post.post.post.user)

    return ( 
        <div>
            <form>
                <label htmlFor="title">Title</label>
                <input type="text" id="title" placeholder={post.post.post.title}/>
                <h2>{post.post.post.user}</h2>
                <label htmlFor="content">content</label>
                <textarea name="content" id="content" cols="30" rows="10">{post.post.post.content}</textarea>
                <p>{post.post.post.time}</p>
                <p>{post.post.post.upvote}</p>
            </form>
        </div>
     );
}

export default Update;
