import {onSnapshot} from 'firebase/firestore'
import { useEffect, useState } from 'react';
import { colRef } from '../firebaseConfig/firebaseConfig'
import { auth } from '../firebaseConfig/firebaseConfig';
import Post from '../component/Post';

const Home = () => {

    const [posts, setPosts] = useState()

    useEffect(()=>{
        let unsubscribe = onSnapshot(colRef,(snapshot)=>{
            let postsContainer = []
            snapshot.docs.forEach(doc=>{
                postsContainer.push({...doc.data(),id:doc.id})
            })
            setPosts(postsContainer)
            console.log(posts)
        });
        return ()=> unsubscribe();
    },[]);

    function changeOrder(e) {
        if(e.target.textContent==='Top') {
            let newPostarray = posts.sort((a,b)=>{
                if(a.upvote>b.upvote) {
                    return 1
                }
                else{
                    return -1
                }
            })
            console.log(newPostarray)
        }
        else if(e.target.textContent==='New') {
            let newPostarray = posts.sort((a,b)=>{
                if(a.time>b.time) {
                    return 1
                }
                else{
                    return -1
                }
            })

            console.log(newPostarray)
        }
        else if(e.target.textContent==='Old') {
            let newPostarray = posts.sort((a,b)=>{
                if(a.time<b.time) {
                    return 1
                }
                else{
                    return -1
                }
            })

            console.log(newPostarray)
        }
    }


    return ( 
        <div>
            <div>
                <button onClick={(e)=>{
                    changeOrder(e)
                }}>Top</button>
                <button onClick={(e)=>{
                    changeOrder(e)
                }}>New</button>
                <button onClick={(e)=>{
                    changeOrder(e)
                }}>Old</button>
            </div>
            <div>
                {posts && posts.map((post)=>{
                    return <Post post={post} key={post.id} />
                })}
            </div>
        </div>

    );
}

export default Home;
