import {onSnapshot} from 'firebase/firestore'
import { useEffect, useState } from 'react';
import { colRef } from '../firebaseConfig/firebaseConfig'
import Post from '../component/Post';

const Home = () => {

    const [posts, setPosts] = useState()

    useEffect(()=>{
        let unsubscribe = onSnapshot(colRef,(snapshot)=>{
            let postsContainer = []
            snapshot.docs.forEach(doc=>{
                postsContainer.push({...doc.data(),id:doc.id})
            })
            setPosts(postsContainer)
            setOrderedPosts(postsContainer)
        });
        return ()=> unsubscribe();
    },[]);



    function changeOrder(e) {
        if(e.targeg.textContent==='Top') {
            console.log('hello')
        }
    }

    return ( 
        <div>
            <div>
                <button onClick={(e)=>{
                    changeOrder(e)
                }}>Top</button>
                <button onClick={(e)=>{
                    changeOrder(e)
                }}>New</button>
                <button onClick={(e)=>{
                    changeOrder(e)
                }}>Old</button>
            </div>
            <div>
                {posts && posts.map((post)=>{
                    return <Post post={post} key={post.id} />
                })}
            </div>
        </div>

    );
}

export default Home;
