import { useParams } from "react-router";
import { useLocation } from "react-router";

const Post = () => {

    const location = useLocation()
    const {post} = location.state
    console.log(post.title)

    return ( 
        <div>
            prova {post.title}
            <h1>{post.title}</h1>
            <h2>{post.user}</h2>
            <p>{post.content}</p>
            {/* <h6>{post.time.toDate().toDateString()}</h6> */}
            <h6>{post.upvote}</h6>
        </div>
     );
}

export default Post;
