import { updateDoc, doc } from "firebase/firestore";
import { useState } from "react/cjs/react.development";
import { db } from "../firebaseConfig/firebaseConfig";
import { Link } from "react-router-dom";

const PostSummary = ({post}) => {
    console.log(post.id)

    const [like,setLike] = useState(0)
    const [unlike,setUnlike] = useState(0)

    function upvote() {
        const docRef = doc(db,'posts',post.id)
        const newUpvote = post.upvote + 1

        if(like==0 && unlike==0) {
            updateDoc(docRef,{
                upvote:newUpvote
            }).then(()=>{
                setLike(1)
            })
        }
        else if(like==0 && unlike==1) {
            updateDoc(docRef,{
                upvote:newUpvote + 1
            }).then(()=>{
                setLike(1)
                setUnlike(0)
            })
        }

        const upvoteElement = document.querySelector('.upvote')
        upvoteElement.style.backgroundColor = 'green'
        const downvoteElement = document.querySelector('.downvote')
        downvoteElement.style.backgroundColor = 'white'

    }

    function downvote() {

        const docRef = doc(db,'posts',post.id)
        const newUpvote = post.upvote - 1

        if(like==0 && unlike==0) {
            updateDoc(docRef,{
                upvote:newUpvote
            }).then(()=>{
                setUnlike(1)
            })
        }
        else if(like==1 && unlike==0) {
            updateDoc(docRef,{
                upvote:newUpvote - 1
            }).then(()=>{
                setLike(0)
                setUnlike(1)
            })
        }

        const upvoteElement = document.querySelector('.upvote')
        upvoteElement.style.backgroundColor = 'white'
        const downvoteElement = document.querySelector('.downvote')
        downvoteElement.style.backgroundColor = 'red'
    }

    return ( 
        <div>
            <Link to={`post/${post.id}`}>
                <h2>{post.title}</h2>
                <p>{post.user}</p>
            </Link>

            <p>{post.content}</p>
            <h6>{post.time}</h6>
            <h6>{post.upvote}</h6>
            <button onClick={upvote}>Upvote</button>
            <button onClick={downvote}>downvote</button>
        </div>
     );
}

export default PostSummary;
