import { addDoc } from "firebase/firestore";
import { colRef } from "../firebaseConfig/firebaseConfig";
import { auth } from "../firebaseConfig/firebaseConfig";
import { storage } from "../firebaseConfig/firebaseConfig";

import { ref,uploadBytesResumable } from "firebase/storage";

const Create = () => {

    async function createPost(e) {
        e.preventDefault()
        const formRef = document.querySelector('form')

        await storageFile(formRef.image.files[0])

        if(auth.currentUser) {

            const title = formRef.title.value
            const content = formRef.content.value

            addDoc(colRef,{
                title:title,
                content: content,
                test1:33
            }).then(()=>{
                formRef.reset()
                console.log('added')
            })
        }
        else{
            console.log('not logged in')
        }

    }


    async function storageFile(file) {
        if(!file) return
        const storageRef = ref(storage,`files/${file.name}`)
        const uploadTask = uploadBytesResumable(storageRef, file)
        uploadTask.on('state_changed', (snapshot)=>{
            const prog = (snapshot.bytesTransferred/snapshot.totalBytes);
            console.log(prog)
        }, (error)=>{console.log(error)})
    }

    return ( 
        <div>
            <form action="" onSubmit={(e)=>{
                createPost(e)
            }}>
                <input type="text" name="title"/>
                <input type="text" name="content"/>
                <input type="file" name="image"/>
                <button>Submit</button>
            </form>
        </div>
     );
}

export default Create;
