import {doc, getDocs, onSnapshot} from 'firebase/firestore'
import { useEffect, useState } from 'react';
import { colRef } from '../firebaseConfig/firebaseConfig'

const Home = () => {

    const [posts, setPosts] = useState()



    useEffect(()=>{
        let unsubscribe = onSnapshot(colRef,(snapshot)=>{
            let postsContainer = []
            snapshot.docs.forEach(doc=>{
                postsContainer.push({...doc.data(),id:doc.id})
            })
            setPosts(postsContainer)
            console.log(posts)
        });
        return ()=> unsubscribe();
    },[]);

    return ( 
        <h1>{posts}</h1>
    );
}

export default Home;
